  PLCY_PRD_ID    	NUMERIC(31)  NOT NULL,
  PLCY_PRD_GRP_ID	NUMERIC(31),
  CALC_LOSS_RT       NUMERIC(15,2),
  NET_LOSS_RT        NUMERIC(15,2),
  EARN_PREM_AMT         NUMERIC(15,2),
  PD_LOSS_AMT 	NUMERIC(15,2),
  INCUR_LOSS_AMT        NUMERIC(15,2),
  INS_SH_INCUR_AMT      NUMERIC(15,2),
  CALC_DTM 		TIMESTAMP,
  PREM_AMT		NUMERIC(15,2),
  OPN_CLM_CNT		NUMERIC(31),
  TOT_CLM_CNT		NUMERIC(31),
  AUDIT_USER_ID_CREA   NUMERIC(31) NOT NULL,
  AUDIT_USER_CREA_DTM  TIMESTAMP NOT NULL,
  AUDIT_USER_ID_UPDT   NUMERIC(31),
