COMMENT ON TABLE POLICY_PERIOD_LOC_GRP_DEPS IS 'This Policy Maintenance subsystem data table captures information about the deposit required for a policy location group on a policy period.';
COMMENT ON COLUMN POLICY_PERIOD_LOC_GRP_DEPS.PPLGD_ID IS 'This is the primary surrogate key for the table used to uniquely identify each record.';
COMMENT ON COLUMN POLICY_PERIOD_LOC_GRP_DEPS.PLCY_PRD_ID IS 'This is a foreign key to the policy_period table specifying which policy period this record is associated with.';
COMMENT ON COLUMN POLICY_PERIOD_LOC_GRP_DEPS.PLCY_PRD_GRP_ID IS 'This is a foreign key to the policy_period_group table specifying which policy period location group this record is associated with.';
COMMENT ON COLUMN POLICY_PERIOD_LOC_GRP_DEPS.PPLGD_EFF_DT IS 'The is the date that the required deposit for the location group became effective.';
COMMENT ON COLUMN POLICY_PERIOD_LOC_GRP_DEPS.PPLGD_END_DT IS 'The is the date that the required deposit for the location group is no longer in effect.';
COMMENT ON COLUMN POLICY_PERIOD_LOC_GRP_DEPS.PPLGD_PREM_AMT is 'This stores the premium portion of the  PPLGD_DEPS_AMT (total required deposit for the lcoation group).';
COMMENT ON COLUMN POLICY_PERIOD_LOC_GRP_DEPS.PPLGD_ASMT_AMT is 'This stores the assessment portion of the  PPLGD_DEPS_AMT (total required deposit for the location group).';
COMMENT ON COLUMN POLICY_PERIOD_LOC_GRP_DEPS.PPLGD_DEPS_AMT is 'This stores total required deposit for the location group (premium and assessments).';
COMMENT ON COLUMN POLICY_PERIOD_LOC_GRP_DEPS.PPLGD_OVRRD_IND IS 'An indicator specifying whether or not the amount or percentage of deposit has been overridden by the user.';
COMMENT ON COLUMN POLICY_PERIOD_LOC_GRP_DEPS.AUDIT_USER_ID_CREA IS 'The user identifier of the user that created this record.';
COMMENT ON COLUMN POLICY_PERIOD_LOC_GRP_DEPS.AUDIT_USER_CREA_DTM IS 'Date and time record was created.  Used for audit purposes.';
COMMENT ON COLUMN POLICY_PERIOD_LOC_GRP_DEPS.AUDIT_USER_ID_UPDT IS 'The user identifier of the user that updated this record.';
COMMENT ON COLUMN POLICY_PERIOD_LOC_GRP_DEPS.AUDIT_USER_UPDT_DTM IS 'Date and time record was updated.  Used for audit purposes.';
COMMENT ON COLUMN POLICY_PERIOD_LOC_GRP_DEPS.VOID_IND IS 'An indicator identifying whether the record has been logically deleted or not.';


CREATE TABLE POLICY_PRD_LOC_GRP_EARN_PREM
(
   PPLGEP_ID        			NUMERIC(31)            not null,
   PLCY_PRD_ID    	 		NUMERIC(31)			   not null,
   PLCY_PRD_GRP_ID 			NUMERIC(31),
   PPLGEP_WRTN_AMT  	 		NUMERIC(15,2),
   PPLGEP_CHG_WRTN_AMT 		NUMERIC(15,2),
   PPLGEP_DEPS_AMT  	 		NUMERIC(15,2),
   PPLGEP_PYRL_AMT  			NUMERIC(15,2),
   PPLGEP_AUDT_AMT   			NUMERIC(15,2),
   PPLGEP_RTSP_AMT   			NUMERIC(15,2),
   PPLGEP_EARN_PREM_AMT  		NUMERIC(15,2),
   PPLGEP_CHG_EARN_PREM_AMT 	NUMERIC(15,2),
   PPLGEP_UNEARN_PREM_AMT 	NUMERIC(15,2),
   PPLGEP_CHG_UNEARN_PREM_AMT NUMERIC(15,2),
   PPLGEP_COMM_PREM_AMT 		NUMERIC(15,2),
   PPLGEP_CHG_COMM_PREM_AMT 	NUMERIC(15,2),
   JUR_TYP_CD				VARCHAR(15),
   PPLGEP_CALC_DT  			TIMESTAMP,
   AUDIT_USER_ID_CREA   	NUMERIC(31),
   AUDIT_USER_CREA_DTM  	TIMESTAMP,
   AUDIT_USER_ID_UPDT   	NUMERIC(31),
   AUDIT_USER_UPDT_DTM  	TIMESTAMP,
   VOID_IND 			  	CHAR(1)          	   not null default 'n',
   constraint XPK_PPLGEP primary key (PPLGEP_ID)
);

COMMENT ON TABLE  POLICY_PRD_LOC_GRP_EARN_PREM is 'This Policy Maintenance - Premium Calculation subsystem table is to store earned premium details for a location group on a policy period.';
COMMENT ON COLUMN POLICY_PRD_LOC_GRP_EARN_PREM.PPLGEP_ID IS 'This is the primary surrogate key for the table used to uniquely identify each record.';
COMMENT ON COLUMN POLICY_PRD_LOC_GRP_EARN_PREM.PLCY_PRD_ID IS 'This is a foreign key to the policy_period table specifying which policy and period this record is for.';
COMMENT ON COLUMN POLICY_PRD_LOC_GRP_EARN_PREM.PLCY_PRD_GRP_ID IS 'This is a foreign key to the policy_period_group table specifying which policy period group this record is for.';
COMMENT ON COLUMN POLICY_PRD_LOC_GRP_EARN_PREM.PPLGEP_WRTN_AMT IS 'This is the written premium amount used in calculation of this earned premium record.';
COMMENT ON COLUMN POLICY_PRD_LOC_GRP_EARN_PREM.PPLGEP_DEPS_AMT IS 'This is the deposit amount used in calculation of this earned premium record.';
COMMENT ON COLUMN POLICY_PRD_LOC_GRP_EARN_PREM.PPLGEP_PYRL_AMT IS 'This is the payroll report premium used in the calculation of this earned premium record.';
COMMENT ON COLUMN POLICY_PRD_LOC_GRP_EARN_PREM.PPLGEP_AUDT_AMT IS 'This is the audited premium used in the calculation of this earned premium record.';
COMMENT ON COLUMN POLICY_PRD_LOC_GRP_EARN_PREM.PPLGEP_RTSP_AMT IS 'This is the retrospective premium used in the calculation of this earned premium record.';
COMMENT ON COLUMN POLICY_PRD_LOC_GRP_EARN_PREM.PPLGEP_EARN_PREM_AMT IS 'This is the actual earned premium amount.';
COMMENT ON COLUMN POLICY_PRD_LOC_GRP_EARN_PREM.PPLGEP_CALC_DT IS 'This is the date that the earned premium amount was calculated (I.e. the valuation date).';
COMMENT ON COLUMN POLICY_PRD_LOC_GRP_EARN_PREM.AUDIT_USER_ID_CREA IS 'The user identifier of the user that created this record.';
COMMENT ON COLUMN POLICY_PRD_LOC_GRP_EARN_PREM.AUDIT_USER_CREA_DTM IS 'Date and time record was created.  Used for audit purposes.';
COMMENT ON COLUMN POLICY_PRD_LOC_GRP_EARN_PREM.AUDIT_USER_ID_UPDT IS 'The user identifier of the user that updated this record.';
COMMENT ON COLUMN POLICY_PRD_LOC_GRP_EARN_PREM.AUDIT_USER_UPDT_DTM IS 'Date and time record was updated.  Used for audit purposes.';
COMMENT ON COLUMN POLICY_PRD_LOC_GRP_EARN_PREM.VOID_IND IS 'An indicator identifying whether the record has been logically deleted or not.';
COMMENT ON COLUMN POLICY_PRD_LOC_GRP_EARN_PREM.PPLGEP_CHG_UNEARN_PREM_AMT IS 'The amount of the last change to the unearned premium amount';
COMMENT ON COLUMN POLICY_PRD_LOC_GRP_EARN_PREM.PPLGEP_UNEARN_PREM_AMT IS 'This is the actual unearned premium amount.';
COMMENT ON COLUMN POLICY_PRD_LOC_GRP_EARN_PREM.PPLGEP_CHG_WRTN_AMT IS 'The amount of the last change to written premium';
COMMENT ON COLUMN POLICY_PRD_LOC_GRP_EARN_PREM.JUR_TYP_CD IS 'Foreign key to JURISDICTION_TYPE table';
COMMENT ON COLUMN POLICY_PRD_LOC_GRP_EARN_PREM.PPLGEP_CHG_EARN_PREM_AMT IS 'The amount of the last change to the earned premium amount';
COMMENT ON COLUMN POLICY_PRD_LOC_GRP_EARN_PREM.PPLGEP_COMM_PREM_AMT is 'represents commissionable premium amount';
COMMENT ON COLUMN POLICY_PRD_LOC_GRP_EARN_PREM.PPLGEP_CHG_COMM_PREM_AMT is 'represents the change in the commissionable premium amount';